import { Router, request, response } from "express";
import { methods as languageController }  from "./../controllers/language.controller";
const router = Router();

router.get("/",languageController.getpersona);
router.get("/",languageController.getTarea);
//router.get("/:fecha",languageController.LecturaFecha);
router.get("/:nombre",languageController.LecturaPersona);
router.get("/:id",languageController.LecturaLenguaje);
router.delete("/:id",languageController.Eliminar);
router.post("/",languageController.addLanguage);
router.put("/:id",languageController.actualizar);


export default router;

